mapper.select:
select uName,uPassword 
from school.user 
where uName = #{uName} 
and uPassword = #{uPassword}

mapper.update:
   UPDATE school.user
	SET
	uName= #{uName}
	uPassword= #{uPassword}
	name = #{name}
	academy = #{academy}
	major = #{major}
	grade = #{grade}
	tel = #{tel}
  WHERE uName = #{uName}
